mapper.update:
UPDATE
qa
SET
qa_title = #{qa_title}
, qa_content = #{qa_content}
WHERE
qa_no = #{qa_no}

mapper.insert:
INSERT INTO
qa
(
member_no
, qa_title
, qa_content
, qa_insertDate
)
VALUES
(
#{member_no}
, #{qa_title}
, #{qa_content}
, NOW()
)

INSERT INTO
navi
(
member_no
, product_no
, insertDate
)
VALUES
(
#{member_no}
, #{product_no}
, #{insertDate}
)
